import React, { useState } from "react";
import NoteContext from "./notesContext";

const NoteState = (props) => {
    const host = "http://127.0.0.1:5000/"
    const noteInitial = []
    const [notes, setNotes] = useState(noteInitial);


    // Add Note
    const addNote = async(title, description, tag) => {
        const response = await fetch(`${host}user/addnotes`, {
            method: "POST",
            headers: {
                'Content-Type': "application/json",
                "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI2NDgzMzJlZWI2ZDc3NTdmOWUwZDVhZmMiLCJpYXQiOjE2ODY2Mzk5NDIsImV4cCI6MTY4NjY1NDM0Mn0.iZvG2z8C-E-R3h9mRzR2X1xhWolwqIZA8dhqzur_xME"
            },
            body: JSON.stringify({title, description, tag})
        });
        const json = response.json();

    }

    // Edit Note
    const editNote = async (id, title, description, tag) => {
        // Api call
        const response = await fetch(`${host}user/updatenote/${id}`, {
            method: "POST",
            headers: {
                'Content-Type': "application/json",
                "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI2NDgzMzJlZWI2ZDc3NTdmOWUwZDVhZmMiLCJpYXQiOjE2ODY2Mzk5NDIsImV4cCI6MTY4NjY1NDM0Mn0.iZvG2z8C-E-R3h9mRzR2X1xhWolwqIZA8dhqzur_xME"
            },
            body: JSON.stringify({title, description, tag})
        });

        // Logic for delete note
        for (let index = 0; index < notes.length; index++) {
            const element = notes[index];
            if (element._id == id) {
                element.title = title;
                element.description = description;
                element.tag = tag;
            }
        }
    }

    // Delete Note
    const deleteNote = (id) => {
        const newNotes = notes.filter((note) => { return note._id !== id });
        setNotes(newNotes);
    }

    return (
        <NoteContext.Provider value={{ notes, addNote, editNote, deleteNote }}>
            {props.children}
        </NoteContext.Provider>
    )
}


export default NoteState
