import React, { useState } from 'react'

const Signup = () => {
  const host = "http://localhost:5000"
  const [credential, setCredential] = useState({ email: "", password: "" })
  const handleSubmit = async (e) => {
      e.preventDefault();
      const response = await fetch(`${host}/user/signup`, {
          method: "POST",
          headers: {
              'Content-Type': "application/json"
          },
          body: JSON.stringify({ email: credential.email, password: credential.password })
      });

      const json = await response.json()
      console.log(json);
      if(json.success) {
          // save the token and redirect
          localStorage.setItem('token', json.token);
          navigate('/');
      } else {
          alert("Invalid Credential")
      }
  }
  const onChange = (e) => {
      setCredential({
          ...credential, [e.target.name]: e.target.value
      })
  }


  return (
    <div className='my-3 mx-2'>
      <form className='my-3' >
        <h2>Signup Here</h2>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name</label>
          <input type="text" className="form-control" id="name" onChange={onChange} name='name' aria-describedby="nameHelp" />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input type="email" className="form-control" id="email" onChange={onChange} name='email' aria-describedby="emailHelp" />
          <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input type="password" className="form-control" id="password" onChange={onChange} name='password' />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  ) 
}

export default Signup;
